mapper.update:
UPDATE tbl_class
title = #{class_title}, category = #{class_category}, target = #{class_target}, price = #{class_price}, introduce = #{class_introduce}, file_path = #{class_file_path}, file_name = #{class_file_name}, file_ext = #{class_file_ext}, teacher_id = #{class_teacher_id},
WHERE id = #{class_id}

mapper.select:
SELECT id, class_id, data_order, title, data_name, created_at, updated_at
FROM tbl_class_data
WHERE id = #{id}
